	public static Result checkInputLen(String str , int min , int max ,String input_name) {
		
		
		if(str.isEmpty()) {
			
			return new ErrorResult(input_name+Messages.input_is_necessary);
			
		}
		
		if(str.length()<min) {
			return new ErrorResult(input_name+Messages.short_input);
		}
		if(str.length()>max) {
			return new ErrorResult(input_name+Messages.long_input);
		}
		return new SuccessResult();
	}
	
	
	
	
	
	
	
	
	
	
	
	
	
